// Verify SignWell webhook signature
const crypto = require('crypto');
const SIGNWELL_WEBHOOK_SECRET = 'YOUR_SIGNWELL_WEBHOOK_SECRET';

const raw = $input.first().json;
const headers = raw.headers || {};
const signature = headers['x-signwell-signature'] || headers['x-webhook-signature'] || '';

if (!signature) {
  throw new Error('Missing SignWell webhook signature. Rejecting request.');
}

const body = raw.rawBody || JSON.stringify(raw.body || raw);
const computed = crypto.createHmac('sha256', SIGNWELL_WEBHOOK_SECRET).update(body).digest('hex');

if (computed !== signature) {
  throw new Error('SignWell webhook signature mismatch. Rejecting request.');
}

return $input.all();

// Extract SignWell data
const payload = $input.first().json;
const doc = payload.data || payload.document || payload;

const clientRecipient = (doc.recipients || []).find(r => r.role === 'Client') || {};

return [{
  json: {
    signwell_doc_id: doc.id || '',
    doc_name: doc.name || doc.title || '',
    completed_pdf_url: doc.completed_pdf || doc.pdf_url || doc.file_url || '',
    client_name: clientRecipient.name || '',
    client_email: (clientRecipient.email || '').toLowerCase()
  }
}];

// Match SignWell doc to Notion contact
const signwell = $('Extract SignWell Data').first().json;
const notionResults = $('Find Notion Contact').all();

let match = null;
for (const result of notionResults) {
  const page = result.json;
  const props = page.properties || {};
  const email = (props.Email?.email || '').toLowerCase();
  if (email === signwell.client_email) {
    match = page; break;
  }
}

if (!match) {
  throw new Error('Could not match SignWell doc to Notion contact. Email: ' + signwell.client_email);
}

const props = match.properties || {};
const status = props.Status?.select?.name || '';

// Idempotency: skip if already onboarded
if (status === 'Project Started' || status === 'Onboarded') {
  throw new Error('Client already onboarded. Skipping duplicate SignWell webhook.');
}

const name = props.Name?.title?.[0]?.plain_text || signwell.client_name;
const company = props.Company?.rich_text?.[0]?.plain_text || '';

// Build Slack channel name
const channelName = 'client-' + (company || name)
  .toLowerCase().replace(/[^a-z0-9]/g, '-').replace(/-+/g, '-').replace(/^-|-$/g, '').substring(0, 60);

return [{
  json: {
    ...signwell,
    notion_page_id: match.id,
    contact_name: name,
    company_name: company,
    slack_channel_name: channelName
  }
}];

// Find the matching project and extract its page ID for status update later
const matchData = $('Match & Validate').first().json;
const projects = $('Find Notion Project').all();

// Match project to client by looking for the contact name or company in the project name
let projectPageId = '';
for (const proj of projects) {
  const page = proj.json;
  const props = page.properties || {};
  const projName = (props['Project Name']?.title?.[0]?.plain_text || '').toLowerCase();
  const company = (matchData.company_name || '').toLowerCase();
  const name = (matchData.contact_name || '').toLowerCase();
  if ((company && projName.includes(company)) || (name && projName.includes(name))) {
    projectPageId = page.id;
    break;
  }
}
// If no match by name, take the first one (most common case: one contract sent at a time)
if (!projectPageId && projects.length > 0) {
  projectPageId = projects[0].json.id;
}

return [{
  json: {
    ...matchData,
    notion_project_id: projectPageId,
    needs_stripe_lookup: true
  }
}];

// Get the customer ID and find their draft invoice
const matchData = $('Prepare Stripe Lookup').first().json;
const stripeResult = $('Stripe Find Customer').first().json;

const customer = stripeResult.data?.[0];
if (!customer) {
  throw new Error('No Stripe customer found for email: ' + matchData.client_email);
}

return [{
  json: {
    ...matchData,
    stripe_customer_id: customer.id
  }
}];

// Extract invoice ID and send it
const matchData = $('Extract Stripe Customer').first().json;
const invoiceResult = $('Stripe Find Draft Invoice').first().json;

const invoice = invoiceResult.data?.[0];
if (!invoice) {
  throw new Error('No draft invoice found for customer: ' + matchData.stripe_customer_id);
}

return [{
  json: {
    ...matchData,
    invoice_id: invoice.id
  }
}];

// Resume subscription if found
const result = $input.first().json;
const sub = result.data?.[0];

return [{
  json: {
    has_subscription: !!sub,
    subscription_id: sub?.id || ''
  }
}];

const searchResult = $input.first().json;
const folderId = searchResult.files?.[0]?.id || '';
if (!folderId) {
  throw new Error('No Drive folder found for client. Cannot upload signed contract.');
}
return [{ json: { drive_folder_id: folderId } }];

